import React, { Component } from 'react';
import * as d3 from "d3";

class BarChart extends Component {
    constructor(props){
        super(props);
    }

    drawChart = (props) => {   
        const margin = 60;
        const width = 1000;
        const height = 600;

        const canvas = d3.select("#root")
                       .append("svg")
                       .attr("width", width)
                       .attr("height", height)
                       .append("g")
                           .attr("transform", "translate(" + margin + "," + margin + ")");

        const yScale = d3.scaleLinear()
                       .domain([0, 500])
                       .range([height - 80, 0]);

        canvas.append("g")
              .call(d3.axisLeft(yScale));


        const xScale = d3.scaleBand()
                       .domain(dataArray.map((d) => d.date))
                       .range([0, width - 80])
                       .padding(0.4)

        canvas.append("g")
              .attr("transform", "translate(0, 520)")
              .call(d3.axisBottom(xScale));


        canvas.selectAll("rect")
              .data(props.dataArray)
              .enter()
                  .append("rect")
                  .attr("x", function(d) {
                      return xScale(d.date);
                  })
                  .attr("y", function(d) {
                      return yScale(d.num);
                  })
                  .attr("height", function(d) {
                      return height - yScale(d.num) - 80;
                  })
                  .attr("width", xScale.bandwidth());

        canvas.append("g")
              .attr("class", "grid")
              .call(d3.axisLeft(yScale)
                      .tickSize(-width, 0, 0)
                      .tickFormat(""));
    }
    render() { 
        return (
        <React.Fragment>
            {this.drawChart()}
        </React.Fragment>
        );
    }
}

export default BarChart;
